import { useState, useEffect, useRef } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./components/Login";
import Signup from "./components/Signup";
import TodoList from "./components/TodoList";
import TodoCreate from "./components/TodoCreate";
import "./App.css";

const App = () => {
  const [todos, setTodos] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const isFirstRender = useRef(true);

  useEffect(() => {
    const storedTodos = localStorage.getItem("todos");
    const storedUser = localStorage.getItem("user");

    if (storedTodos) {
      try {
        const parsedTodos = JSON.parse(storedTodos);
        if (Array.isArray(parsedTodos)) {
          setTodos(parsedTodos);
          console.log("Loaded todos:", parsedTodos);
        }
      } catch (error) {
        console.error("Error parsing todos:", error);
        setTodos([]);
      }
    }

    if (storedUser) {
      setIsAuthenticated(true);
    }
  }, []);

  useEffect(() => {
    if (isFirstRender.current) {
      isFirstRender.current = false;
      return;
    }
    if (todos.length > 0) {
      localStorage.setItem("todos", JSON.stringify(todos));
      console.log("Saved todos:", todos);
    }
  }, [todos]);

  const handleLogin = () => {
    setIsAuthenticated(true);
    localStorage.setItem("user", JSON.stringify({ authenticated: true }));
    sessionStorage.setItem("isAuthenticated", "true");
  };

  const handleLogout = () => {
    sessionStorage.setItem("isAuthenticated", "false");
    setIsAuthenticated(false);
  };

  const createTodo = (title, category, priority, dueDate) => {
    const newTodo = {
      id: crypto.randomUUID(),
      title,
      completed: false,
      category,
      priority,
      dueDate,
    };
    setTodos((prevTodos) => [...prevTodos, newTodo]);
  };

  const removeTodo = (id) => {
    setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== id));
  };

  const changeTodo = (id, title, category, priority, dueDate, completed) => {
    const updatedTodo = { id, title, category, priority, dueDate, completed };
    setTodos((prevTodos) =>
      prevTodos.map((todo) => (todo.id === id ? updatedTodo : todo))
    );
  };

  useEffect(() => {
    if (!("Notification" in window)) {
      console.log("Browser does not support notifications.");
      return;
    }

    Notification.requestPermission().then((permission) => {
      if (permission === "granted") {
        const checkReminders = () => {
          todos.forEach((todo) => {
            const dueTime = new Date(todo.dueDate).getTime();
            const currentTime = new Date().getTime();

            if (dueTime - currentTime <= 0 && !todo.completed) {
              new Notification(`Reminder: ${todo.title}`, {
                body: `Due: ${todo.dueDate}`,
                icon: "/path/to/icon.png",
              });
            }
          });
        };

        const interval = setInterval(checkReminders, 60000); // Every minute
        return () => clearInterval(interval);
      }
    });
  }, [todos]);

  const [sortBy, setSortBy] = useState("priority");

  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  // ✅ Pass `todos` and `sortBy` to ensure correct sorting
  const sortedTodos = sortTodos(todos, sortBy);

  const sortTodos = (todos, sortBy) => {
    const priorityOrder = { High: 1, Medium: 2, Low: 3 };

    return [...todos].sort((a, b) => {
      // When sorting by "Completed" → Completed tasks go to the top
      if (sortBy === "completed") {
        if (a.completed !== b.completed) {
          return a.completed ? -1 : 1; // Completed first
        }
        return new Date(a.dueDate) - new Date(b.dueDate);
      }

      // For other sorting options → Completed tasks go to the bottom
      if (a.completed !== b.completed) {
        return a.completed ? 1 : -1; // Completed at the bottom
      }

      // Sorting logic for other options
      if (sortBy === "priority") {
        return priorityOrder[a.priority] - priorityOrder[b.priority];
      }

      if (sortBy === "dueDate") {
        return new Date(a.dueDate) - new Date(b.dueDate);
      }

      if (sortBy === "title") {
        return a.title.localeCompare(b.title);
      }

      if (sortBy === "category") {
        return a.category.localeCompare(b.category);
      }

      return 0;
    });
  };

  return (
    <Router>
      {isAuthenticated ? (
        <>
          <header className="header">
            <div className="headerBlock">
              <h1>My Todo List</h1>
              <div className="controls"> 
                <button onClick={handleLogout} className="logout-btn">Logout</button>

                <div className="sort-dropdown">
                  <label htmlFor="sort">Sort by:</label>
                  <select id="sort" value={sortBy} onChange={handleSortChange}>
                    <option value="priority">Priority</option>
                    <option value="title">Title</option>
                    <option value="dueDate">Due Date</option>
                    <option value="category">Category</option>
                    <option value="completed">Completed</option>
                  </select>
                </div>
              </div>
            </div>
          </header>

          <div className="listBlock">
            <TodoList todos={todos} removeTodo={removeTodo} changeTodo={changeTodo} />
          </div>

          <div className="createBlock">
            <TodoCreate createTodo={createTodo} />
          </div>
        </>
      ) : (
        <Routes>
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/" element={<Navigate to="/login" replace />} />
        </Routes>
      )}
    </Router>
  );
};

export default App;
