import { useState, useEffect, useRef } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./components/Login";
import Signup from "./components/Signup";
import TodoList from "./components/TodoList";
import TodoCreate from "./components/TodoCreate";
import "./App.css";

const App = () => {
  const [todos, setTodos] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(null);
  const [darkMode, setDarkMode] = useState(() => {
    return localStorage.getItem("darkMode") === "true";
  });

  const isFirstRender = useRef(true);

  // ✅ Load todos and authentication from localStorage
  useEffect(() => {
    const storedTodos = localStorage.getItem("todos");
    const storedUser = sessionStorage.getItem("user");

    if (storedTodos) {
      try {
        const parsedTodos = JSON.parse(storedTodos);
        if (Array.isArray(parsedTodos)) {
          setTodos(parsedTodos);
          console.log("Loaded todos:", parsedTodos);
        }
      } catch (error) {
        console.error("Error parsing todos:", error);
        setTodos([]);
      }
    }

    setIsAuthenticated(!!storedUser);
  }, []);

  // ✅ Save todos and dark mode preference to localStorage
  useEffect(() => {
    if (isFirstRender.current) {
      isFirstRender.current = false;
      return;
    }
    localStorage.setItem("todos", JSON.stringify(todos));
    localStorage.setItem("darkMode", darkMode);
    document.body.classList.toggle("dark", darkMode);
  }, [todos, darkMode]);

  // ✅ Handle login/logout
  const handleLogin = () => {
    setIsAuthenticated(true);
    sessionStorage.setItem("user", JSON.stringify({ authenticated: true }));
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setIsAuthenticated(false);
  };

  // ✅ Toggle Dark Mode
  const toggleDarkMode = () => {
    setDarkMode((prevMode) => !prevMode);
  };

  // ✅ Create a new todo
  const createTodo = (title, category, priority, dueDate) => {
    const newTodo = {
      id: crypto.randomUUID(),
      title,
      completed: false,
      category,
      priority,
      dueDate,
    };
    setTodos((prevTodos) => [...prevTodos, newTodo]);
  };

  // ✅ Remove todo
  const removeTodo = (id) => {
    setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== id));
  };

  // ✅ Modify existing todo
  const changeTodo = (id, title, category, priority, dueDate, completed) => {
    const updatedTodo = { id, title, category, priority, dueDate, completed };
    setTodos((prevTodos) =>
      prevTodos.map((todo) => (todo.id === id ? updatedTodo : todo))
    );
  };

  // ✅ Prevent rendering before checking authentication
  if (isAuthenticated === null) {
    return <div>Loading...</div>;
  }

  return (
    <Router>
      <Routes>
        {isAuthenticated ? (
          <>
            <Route
              path="/"
              element={
                <>
                  <header className="header">
                    <div className="headerBlock">
                      <h1>My Todos</h1>
                      <div className="btn-group">
                        <button onClick={handleLogout} className="logout-btn">Logout</button>
                        <button onClick={toggleDarkMode} className="toggle-btn">
                          {darkMode ? "Light Mode" : "Dark Mode"}
                        </button>
                      </div>
                    </div>
                  </header>

                  <div className="listBlock">
                    <TodoList todos={todos} removeTodo={removeTodo} changeTodo={changeTodo} />
                  </div>

                  <div className="createBlock">
                    <TodoCreate createTodo={createTodo} />
                  </div>
                </>
              }
            />
            <Route path="*" element={<Navigate to="/" replace />} />
          </>
        ) : (
          <>
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login onLogin={handleLogin} />} />
            <Route path="*" element={<Navigate to="/login" replace />} />
          </>
        )}
      </Routes>
    </Router>
  );
};

export default App;
