import { useState, useEffect, useRef } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./components/Login";
import Signup from "./components/Signup";
import TodoList from "./components/TodoList";
import TodoCreate from "./components/TodoCreate";
import "./App.css";

const App = () => {
  const [todos, setTodos] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(null);
  const isFirstRender = useRef(true);

  // ✅ Load theme, todos, and authentication
  useEffect(() => {
    const storedTheme = localStorage.getItem("theme");
    if (storedTheme) {
      setIsDarkMode(storedTheme === "dark");
    }

    const storedTodos = localStorage.getItem("todos");
    const storedUser = sessionStorage.getItem("user");

    if (storedTodos) {
      try {
        const parsedTodos = JSON.parse(storedTodos);
        if (Array.isArray(parsedTodos)) {
          setTodos(parsedTodos);
        }
      } catch (error) {
        console.error("Error parsing todos:", error);
        setTodos([]);
      }
    }

    setIsAuthenticated(!!storedUser);
  }, []);

  // ✅ Save theme and todos to localStorage
  useEffect(() => {
    localStorage.setItem("theme", isDarkMode ? "dark" : "light");

    if (isFirstRender.current) {
      isFirstRender.current = false;
      return;
    }
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos, isDarkMode]);

  // ✅ Notifications & Reminders
  useEffect(() => {
    if (!("Notification" in window)) {
      console.log("This browser does not support desktop notification");
      return;
    }

    if (Notification.permission !== "granted") {
      Notification.requestPermission().then(permission => {
        if (permission === "granted") {
          console.log("Notification permission granted");
        }
      });
    }

    const checkReminders = () => {
      const now = new Date();
      todos.forEach(todo => {
        if (!todo.completed && todo.dueDate) {
          try {
            const dueDate = new Date(todo.dueDate);
            if (dueDate.toDateString() !== now.toDateString()) {
              return;  // Skip if not due today
            }

            const timeDiff = dueDate - now;
            if (timeDiff <= 300000 || timeDiff < 0) {
              new Notification(`Todo Reminder: ${todo.title}`, {
                body: `Due: ${dueDate.toLocaleString()}\nCategory: ${todo.category || 'No category'}`,
                icon: "/todo-icon.png",
                tag: `todo-reminder-${todo.id}`
              });
            }
          } catch (e) {
            console.error("Error processing todo due date:", e);
          }
        }
      });
    };

    checkReminders();
    const interval = setInterval(checkReminders, 60000);

    return () => clearInterval(interval);
  }, [todos]);

  if (isAuthenticated === null) {
    return <div>Loading...</div>;
  }

  const handleLogin = () => {
    setIsAuthenticated(true);
    sessionStorage.setItem("user", JSON.stringify({ authenticated: true }));
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setIsAuthenticated(false);
  };

  const createTodo = (title, category, priority, dueDate) => {
    const newTodo = {
      id: crypto.randomUUID(),
      title,
      completed: false,
      category,
      priority,
      dueDate,
    };
    setTodos((prevTodos) => [...prevTodos, newTodo]);
  };

  const removeTodo = (id) => {
    setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== id));
  };

  const changeTodo = (id, title, category, priority, dueDate, completed) => {
    const updatedTodo = { id, title, category, priority, dueDate, completed };
    setTodos((prevTodos) =>
      prevTodos.map((todo) => (todo.id === id ? updatedTodo : todo))
    );
  };

  return (
    <Router>
      <div className={isDarkMode ? "dark" : "light"}> 
        <Routes>
          {isAuthenticated ? (
            <>
              <Route
                path="/"
                element={
                  <>
                    <header className="header">
                      <div className="headerBlock">
                        <h1>My Todos</h1>
                        <div className="actions">
                          <button onClick={handleLogout} className="logout-btn">Logout</button>
                        </div>
                      </div>
                    </header>

                    <div className="listBlock">
                      <TodoList
                        todos={todos}
                        removeTodo={removeTodo}
                        changeTodo={changeTodo}
                      />
                    </div>

                    <div className="createBlock">
                      <TodoCreate createTodo={createTodo} />
                    </div>
                  </>
                }
              />
              <Route path="*" element={<Navigate to="/" />} />
            </>
          ) : (
            <>
              <Route path="/login" element={<Login onLogin={handleLogin} />} />
              <Route path="/signup" element={<Signup />} />
              <Route path="*" element={<Navigate to="/login" />} />
            </>
          )}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
