import { useState, useEffect, useRef } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./components/Login";
import Signup from "./components/Signup";
import TodoList from "./components/TodoList";
import TodoCreate from "./components/TodoCreate";
import "./App.css";

const App = () => {
  const [todos, setTodos] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(null); // Start as null to avoid incorrect first render
  const isFirstRender = useRef(true);

  // ✅ Load todos and user authentication from localStorage
  useEffect(() => {
    const storedTodos = localStorage.getItem("todos");
    const storedUser = localStorage.getItem("user");

    if (storedTodos) {
      try {
        const parsedTodos = JSON.parse(storedTodos);
        if (Array.isArray(parsedTodos)) {
          setTodos(parsedTodos);
          console.log("Loaded todos:", parsedTodos);
        }
      } catch (error) {
        console.error("Error parsing todos:", error);
        setTodos([]);
      }
    }

    setIsAuthenticated(storedUser ? true : false); // ✅ Prevents first render from showing TodoList
  }, []);

  // ✅ Prevent rendering before checking auth
  if (isAuthenticated === null) {
    return <div>Loading...</div>; // Temporary loading state
  }

  // ✅ Handle login/logout
  const handleLogin = () => {
    setIsAuthenticated(true);
    localStorage.setItem("user", JSON.stringify({ authenticated: true }));
  };

  const handleLogout = () => {
    localStorage.removeItem("user"); // ✅ Ensure logout clears auth
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <Routes>
        {isAuthenticated ? (
          <>
            <Route
              path="/"
              element={
                <>
                  <header className="header">
                    <div className="headerBlock">
                      <h1>My Todos</h1>
                      <button onClick={handleLogout} className="logout-btn">Logout</button>
                    </div>
                  </header>

                  <div className="listBlock">
                    <TodoList todos={todos} removeTodo={removeTodo} changeTodo={changeTodo} />
                  </div>

                  <div className="createBlock">
                    <TodoCreate createTodo={createTodo} />
                  </div>
                </>
              }
            />
            <Route path="*" element={<Navigate to="/" replace />} />
          </>
        ) : (
          <>
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login onLogin={handleLogin} />} />
            <Route path="*" element={<Navigate to="/login" replace />} />
          </>
        )}
      </Routes>
    </Router>
  );
};

export default App;
