// Updated App.js with fixes for localStorage wiping issue
import { useState, useEffect, useRef } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./components/Login";
import Signup from "./components/Signup";
import TodoList from "./components/TodoList";
import TodoCreate from "./components/TodoCreate";
import "./App.css";

const App = () => {
  const [todos, setTodos] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const hasLoadedTodos = useRef(false); // Flag to ensure todos load only once

  // Load todos and user authentication from localStorage
  useEffect(() => {
    const storedTodos = localStorage.getItem("todos");
    const storedUser = localStorage.getItem("user");

    if (storedTodos) {
      try {
        const parsedTodos = JSON.parse(storedTodos);
        if (Array.isArray(parsedTodos)) {
          setTodos(parsedTodos);
          console.log("Parsed todos on load:", parsedTodos);
        } else {
          console.warn("Stored todos not an array. Resetting.");
          setTodos([]);
        }
      } catch (error) {
        console.error("Error parsing todos:", error);
        setTodos([]);
      }
    } else {
      console.log("No stored todos found.");
    }

    if (storedUser) {
      setIsAuthenticated(true);
    }

    hasLoadedTodos.current = true; // Mark as loaded
  }, []);

  // Save todos to localStorage only after initial load
  useEffect(() => {
    if (hasLoadedTodos.current) {
      localStorage.setItem("todos", JSON.stringify(todos));
      console.log("Saved todos:", todos);
    }
  }, [todos]);

  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  const createTodo = (title, category, priority, dueDate) => {
    const newTodo = {
      id: crypto.randomUUID(),
      title,
      completed: false,
      category,
      priority,
      dueDate,
    };
    console.log("Creating new todo:", newTodo);
    setTodos((prevTodos) => [...prevTodos, newTodo]);
  };

  const removeTodo = (id) => {
    setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== id));
  };

  const changeTodo = (id, title, category, priority, dueDate, completed) => {
    const updatedTodo = { id, title, category, priority, dueDate, completed };
    setTodos((prevTodos) =>
      prevTodos.map((todo) => (todo.id === id ? updatedTodo : todo))
    );
  };

  return (
    <Router>
      <Routes>
        <Route path="/signup" element={<Signup />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route
          path="/"
          element={
            isAuthenticated ? (
              <main className="main">
                <h1>My Todo Lists</h1>
                <TodoList todos={todos} removeTodo={removeTodo} changeTodo={changeTodo} />
                <TodoCreate createTodo={createTodo} />
              </main>
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
