import { useState } from "react";
import TodoShow from "./TodoShow";

const TodoList = ({ todos, removeTodo, changeTodo }) => {
  const [sortBy, setSortBy] = useState("priority");

  // Sorting logic
  const sortTodos = (todos, sortBy) => {
    const priorityOrder = { High: 1, Medium: 2, Low: 3 };
  
    return [...todos]
      .sort((a, b) => {
        // First, sort by completed status (completed tasks at the bottom)
        if (a.completed !== b.completed) {
          return a.completed ? 1 : -1;
        }
  
        // Then, sort by the selected option
        switch (sortBy) {
          case "Priority":
            return (priorityOrder[a.priority] || 4) - (priorityOrder[b.priority] || 4);
          case "Due Date":
            return new Date(a.dueDate) - new Date(b.dueDate);
          case "Title":
            return a.title.localeCompare(b.title);
          case "Category":
            return a.category.localeCompare(b.category);
          default:
            return 0;
        }
      });
  };

  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  const sortedTodos = sortTodos(todos);

  const renderedTodos = sortedTodos.map((todo) => (
    <TodoShow
      key={todo.id}
      todo={todo}
      removeTodo={removeTodo}
      changeTodo={changeTodo}
    />
  ));

  return (
    <div className="main">
      <div className="sort-dropdown">
        <label htmlFor="sort">Sort by:</label>
        <select id="sort" value={sortBy} onChange={handleSortChange}>
          <option value="priority">Priority</option>
          <option value="title">Title</option>
          <option value="dueDate">Due Date</option>
          <option value="completed">Completed</option>
          <option value="category">Category</option>
        </select>
      </div>

      <ul className="todo-list">{renderedTodos}</ul>
    </div>
  );
};

export default TodoList;
