import { useState } from "react";

const TodoCreate = ({ createTodo }) => {
  const [title, setTitle] = useState("");
  const [category, setCategory] = useState("Personal");
  const [priority, setPriority] = useState("Medium");
  const [dueDate, setDueDate] = useState("");

  const handleChangeTitle = (e) => {
    setTitle(e.target.value);
  };

  const handleChangeCategory = (e) => {
    setCategory(e.target.value);
  };

  const handleChangePriority = (e) => {
    setPriority(e.target.value);
  };

  const handleChangeDueDate = (e) => {
    setDueDate(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    createTodo(title, category, priority, dueDate);
    setTitle("");
    setCategory("Personal");
    setPriority("Medium");
    setDueDate("");
  };

  // ✅ Disable button if title or due date is empty
  const isDisabled = !title.trim() || !dueDate;

  return (
    <form onSubmit={handleSubmit} className="todo-create">
      <input
        type="text"
        name="title"
        id="title"
        placeholder="Enter a todo"
        value={title}
        onChange={handleChangeTitle}
        required
      />
      <select value={category} onChange={handleChangeCategory}>
        <option value="Chores">Chores</option>
        <option value="Work">Work</option>
        <option value="Personal">Personal</option>
      </select>
      <select value={priority} onChange={handleChangePriority}>
        <option value="Low">Low</option>
        <option value="Medium">Medium</option>
        <option value="High">High</option>
      </select>
      <input
        type="date"
        value={dueDate}
        onChange={handleChangeDueDate}
        required
      />
      {/* ✅ Disable button when title or due date is missing */}
      <button type="submit" disabled={isDisabled}>
        Add Todo
      </button>
    </form>
  );
};

export default TodoCreate;
