import { useState } from "react";
import TodoShow from "./TodoShow";

const TodoList = ({ todos, removeTodo, changeTodo }) => {
  const [sortBy, setSortBy] = useState("priority");

  // Sorting logic
  const sortTodos = (todos) => {
    switch (sortBy) {
      case "title":
        return [...todos].sort((a, b) => a.title.localeCompare(b.title));
      case "priority":
        return [...todos].sort((a, b) => b.priority - a.priority);
      case "dueDate":
        return [...todos].sort((a, b) => new Date(a.dueDate) - new Date(b.dueDate));
      case "completed":
        return [...todos].sort((a, b) => a.completed - b.completed);
      default:
        return todos;
    }
  };

  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  const sortedTodos = sortTodos(todos);

  const renderedTodos = sortedTodos.map((todo) => (
    <TodoShow
      key={todo.id}
      todo={todo}
      removeTodo={removeTodo}
      changeTodo={changeTodo}
    />
  ));

  return (
    <div className="main">
      <div className="sort-dropdown">
        <label htmlFor="sort">Sort by:</label>
        <select id="sort" value={sortBy} onChange={handleSortChange}>
          <option value="priority">Priority</option>
          <option value="title">Title</option>
          <option value="dueDate">Due Date</option>
          <option value="completed">Completed</option>
        </select>
      </div>

      <ul className="todo-list">{renderedTodos}</ul>
    </div>
  );
};

export default TodoList;
